    private void btnCalculoActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnCalculoActionPerformed

        proy.Buscar(String.valueOf(CBelegirActivo.getSelectedItem()));
        float deprelinearecta = 0;
        float depresuma[] = new float[Integer.parseInt(proy.vidaUtil)];
         DecimalFormat df = new DecimalFormat("#");
        df.setMaximumFractionDigits(4);

        if (rbSumaDigitos.isSelected()) {

            depresuma = proy.SumaDigitos(Integer.parseInt(proy.valorActivo), Integer.parseInt(proy.valorRescate), Integer.parseInt(proy.vidaUtil));
            DefaultTableModel model=(DefaultTableModel)jTabla.getModel();
            float depacum=0;
            for(int i=0; i<Integer.parseInt(proy.vidaUtil);i++){
            
                int year= Integer.parseInt(proy.vidaUtil)-i;
                depacum=depresuma[i]+depacum;
                float vfinal= Integer.parseInt(proy.valorActivo)-depacum;
             
            Object[] row={Integer.toString(year),proy.codigo,proy.tipoActivo,proy.valorActivo,proy.valorRescate,"Suma Digitos",df.format(depresuma[i]),df.format(depacum),df.format(vfinal)};
            model.addRow(row); 
            }
        }
        if (RbLineaRecta.isSelected()) {
            
            DefaultTableModel model=(DefaultTableModel)jTabla.getModel();
            deprelinearecta = proy.LineaRecta(Integer.parseInt(proy.valorActivo), Integer.parseInt(proy.valorRescate), Integer.parseInt(proy.vidaUtil));
            String datosdepre="";
            
            for(int i=0; i<Integer.parseInt(proy.vidaUtil);i++){
            int year= i+1;
            float depacum=deprelinearecta*(i+1);
            float vfinal= Integer.parseInt(proy.valorActivo)-depacum;
           
            datosdepre= datosdepre+year+";"+df.format(deprelinearecta)+";"+df.format(depacum)+";"+df.format(vfinal)+";";
          
            Object[] row={Integer.toString(year),proy.codigo,proy.tipoActivo,proy.valorActivo,proy.valorRescate,"Línea Recta",df.format(deprelinearecta),df.format(depacum),df.format(vfinal)};
            model.addRow(row); 
            
            }
            
            if (proy.BuscarDepreciacion(proy.codigo)==false){
            proy.DepreciacionArrayList.add(proy.codigo+";"+datosdepre);
            }else{
            
                proy.BuscarEliminarDepreciacion(proy.codigo);
                proy.DepreciacionArrayList.add(proy.codigo+";"+datosdepre);
            }
        }

    }//GEN-LAST:event_btnCalculoActionPerformed

