    private void BtnagregarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_BtnagregarActionPerformed
        String cod;

        boolean nom = proy.validartexto(txtNombre.getText());
        boolean co = proy.validartexto(txtcodigo.getText());
        boolean vact = proy.validartexto(txtValorActivo.getText());
        boolean valResc = proy.validartexto(txtValorRescate.getText());
        boolean resp = proy.validartexto(txtResponsable.getText());
        boolean vidaUtil = proy.validartexto(txtVidaUtil.getText());
        
      
        if ((nom == false) || (co == false) || (vact == false) || (valResc == false) || (resp == false) || (vidaUtil == false)) {
            JOptionPane.showMessageDialog(null, "Algunos campos estan vacios");
            
        } else if (Integer.parseInt(txtVidaUtil.getText())>10){
        JOptionPane.showMessageDialog(null, "La vida útil del activo debe ser menor a 10 años");
        
        }else if (Integer.parseInt(txtValorRescate.getText()) < Integer.parseInt(txtValorActivo.getText())) {
            
        DefaultComboBoxModel model =(DefaultComboBoxModel)CBelegirActivo.getModel();// Traer el modelo del combobox
        
        if(model.getIndexOf(txtcodigo.getText())!=-1){ //Valida si el código ya existe en el combobox
        JOptionPane.showMessageDialog(null, "Ya existe un activo con el código:"+txtcodigo.getText());
        } 
        else{ 
            cod = proy.CapturarDatos(txtNombre.getText(), txtcodigo.getText(), TipoActivo.getSelectedItem().toString(), txtValorActivo.getText(), txtValorRescate.getText(), txtResponsable.getText(), txtVidaUtil.getText());
            CBelegirActivo.addItem(cod);

            boolean act = proy.validartexto(CBelegirActivo.getSelectedItem().toString());
            if (act == true) {
                btnCalculo.setEnabled(true);
            }
        }
        }else{
            JOptionPane.showMessageDialog(null, "El valor del activo debe ser menor a valor de rescate.");
        }
        
    }//GEN-LAST:event_BtnagregarActionPerformed

    private void BtnlimpiarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_BtnlimpiarActionPerformed
        txtcodigo.setText(null);
        txtNombre.setText(null);
        txtValorActivo.setText(null);
        txtValorRescate.setText(null);
        txtResponsable.setText(null);
        txtVidaUtil.setText(null);
    }//GEN-LAST:event_BtnlimpiarActionPerformed

